import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import { UserContext } from "../UserContext";
import { useHistory } from "react-router-dom";

export default function RegisterPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [registrationError, setRegistrationError] = useState(null);
  const [redirect, setRedirect] = useState(false);
  const { setUserInfo } = useContext(UserContext);
  const history = useHistory();

  async function register(ev) {
    ev.preventDefault();

    try {
      const response = await fetch("http://localhost:4000/register", {
        method: "POST",
        body: JSON.stringify({ username, password }),
        headers: { "Content-Type": "application/json" },
      });

      if (response.ok) {
        alert('Registration successful');
        // Additional logic if registration is successful (e.g., redirect to login page)
      } else {
        // If the server responds with an error, set the error message
        const errorData = await response.json();
        setRegistrationError(errorData.message || 'Registration failed');
      }
    } catch (error) {
      console.error("Registration error:", error);
      setRegistrationError('An unexpected error occurred during registration');
    }
  }
  if (redirect) {
    // Redirect to the index page after successful registration
    // history.push("/") can also be used here instead of returning <Navigate to={'/'} />
    return <Navigate to={"/"} />;
  }



  return (
    <form className="register" onSubmit={register}>
      <h1>Register</h1>
      {registrationError && <p className="error-message">{registrationError}</p>}
      <input
        type="text"
        placeholder="username"
        value={username}
        onChange={(ev) => setUsername(ev.target.value)}
      />
      <input
        type="password"
        placeholder="password"
        value={password}
        onChange={(ev) => setPassword(ev.target.value)}
      />
      <button>Register</button>
    </form>
  );
}
