import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

// Define modules outside of the component
const modules = {
    toolbar: [
        [{ 'header': '1' }, { 'header': '2' }, { 'font': [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike', 'blockquote'],
        [{ 'list': 'ordered' }, { 'list': 'bullet' },
        { 'indent': '-1' }, { 'indent': '+1' }],
        ['link', 'image', 'video'],
        ['clean'],
    ],
};

export default function Editor({ value, onChange, formats }) {
    return (
        <ReactQuill 
            value={value} 
            theme={'snow'}
            onChange={onChange} 
            modules={modules} 
            formats={formats} 
        />
    );
}
